    /*
     * Editing a movimiento
     */
    function edit($id_mov)
    {   
        // check if the movimiento exists before trying to edit it
        $data['movimiento'] = $this->Movimiento_model->get_movimiento($id_mov);
        
        if(isset($data['movimiento']['id_mov']))
        {
            if(isset($_POST) && count($_POST) > 0)     
            {   
                $params = array(
					'id_camp' => $this->input->post('id_camp'),
					'fecha_mov' => $this->input->post('fecha_mov'),
					'tipo_mov' => $this->input->post('tipo_mov'),
					'cant_ingreso' => $this->input->post('cant_ingreso'),
					'cant_egreso' => $this->input->post('cant_egreso'),
					'cant_saldo' => $this->input->post('cant_saldo'),
					'monto_ingreso' => $this->input->post('monto_ingreso'),
					'monto_salida' => $this->input->post('monto_salida'),
					'monto_saldo' => $this->input->post('monto_saldo'),
					'estado_mov' => $this->input->post('estado_mov'),
					'detalle_mov' => $this->input->post('detalle_mov'),
                );

                $this->Movimiento_model->update_movimiento($id_mov,$params);            
                redirect('movimiento/index');
            }
            else
            {
                $data['_view'] = 'movimiento/edit';
                $this->load->view('layouts/main',$data);
            }
        }
        else
