    } 

    /*
     * Deleting movimiento
     */
    function remove($id_mov)
    {
        $movimiento = $this->Movimiento_model->get_movimiento($id_mov);

        // check if the movimiento exists before trying to delete it
        if(isset($movimiento['id_mov']))
        {
            $this->Movimiento_model->delete_movimiento($id_mov);
            redirect('movimiento/index');
        }
        else
