class Campeonato_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    /*
     * Get campeonato by id_camp
     */
    function get_campeonato($id_camp)
    {
        return $this->db->get_where('campeonato',array('id_camp'=>$id_camp))->row_array();
    }
        
    /*
     * Get all campeonato
     */
    function get_all_campeonato()
    {
        $this->db->select('c.*, g.nombre_gestion, tc.nombre_tipo_camp');
        $this->db->from('campeonato c');
        $this->db->join('gestion g', 'g.id_gestion = c.id_gestion', 'left');
        $this->db->join('tipo_campeonato tc', 'tc.id_tipo_camp = c.id_tipo_camp', 'left');
        $this->db->order_by('c.id_camp', 'desc');
        return $this->db->get()->result_array();
    }
        
    /*
     * function to add new campeonato
     */
    function add_campeonato($params)
    {
        $this->db->insert('campeonato',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update campeonato
     */
    function update_campeonato($id_camp,$params)
    {
        $this->db->where('id_camp',$id_camp);
        return $this->db->update('campeonato',$params);
    }
    
    /*
     * function to delete campeonato
     */
    function delete_campeonato($id_camp)
    {
        return $this->db->delete('campeonato',array('id_camp'=>$id_camp));
    }
}
